import "./App.css";
import Search from "./component/search/search";
import CurrentWeather from "./component/current_weather/current_weather";
import { WEATHER_API_KEY,WEATHER_API_URL} from "./api";
import { useState } from "react";

function App() {
  const [currentWeather,setcurrentWeather] = useState(null)
  const [forecast,setforecast] = useState(null)

  const onhandleOnSearchChanage = (searchData) => {
    const [lat,lon] = searchData.value.split(" ")

    const CurrentWeatherFeth = fetch(`${WEATHER_API_URL}/weather?lat=${lat}&lon=${lon}&appid=${WEATHER_API_KEY}`)
    const forecastFetch = fetch(`${WEATHER_API_URL}/forecast?lat=${lat}&lon=${lon}&appid=${WEATHER_API_KEY}`)

    Promise.all([CurrentWeatherFeth,forecastFetch])
    .then(async (response) => {
      const weatherResponse = await response[0].json()
      const forecastResponse = await response[0].json()

      setcurrentWeather({city: searchData.label , ...weatherResponse})
      setforecast({city: searchData.label, ...forecastResponse})
    })
    .catch((err) => console.log(err))
  }
  console.log(currentWeather,forecast)
  return (
    <div className="container">
      <Search onSearchChange={onhandleOnSearchChanage} />
      <CurrentWeather/>
    </div>
  );
}

export default App;
