import { useState } from "react";
import { AsyncPaginate } from "react-select-async-paginate";
import { GEO_URL, geo_url_options } from "../../api";

const Search = ({ onSearchChange }) => {
  const [search, setSearch] = useState(null);

  const loadOptions = async (inputValue) => {
    try {
      const response = await fetch(`${GEO_URL}/cities?minPopulation=1000000&namePrefix=${inputValue}`, geo_url_options);
      const responseData = await response.json();
  
      // Map the response data to the format expected by react-select
      const options = responseData.map((city) => ({
        value: city.id, // Assuming city.id is the unique identifier for the city
        label: city.name // Assuming city.name is the name of the city
      }));
  
      // Return the options in the expected format
      return { options };
    } catch (err) {
      console.error("Error loading options:", err);
      return { options: [] }; // Return an empty array of options in case of an error
    }
  };
  

  const handleOnchange = (searchData) => {
    setSearch(searchData);
    onSearchChange(searchData);
  };
  return (
    <AsyncPaginate
      placeholder="seach for the city"
      debounceTimeout={600}
      value={search}
      onChange={handleOnchange}
      loadOptions={loadOptions}
    />
  );
};

export default Search;
